const valuesToUpdate = [];

// Liste de tous les champs √† inclure dans le payload
const allFields = Object.keys(fieldMappings);

for (const fieldName of allFields) {
    const fieldMapping = fieldMappings[fieldName];
    const geminiValue = extractedData[fieldName];

    if (!fieldMapping) {
        console.warn(`[WARNING] Field mapping not found for "${fieldName}". Skipping.`);
        continue;
    }

    // Gestion des valeurs "NA"
    if (!geminiValue || (typeof geminiValue === 'string' && geminiValue.toLowerCase() === "na") || (Array.isArray(geminiValue) && geminiValue.length === 0)) {
        // Pour les champs multi-select, on envoie un tableau vide
        if (fieldMapping.type === "select_multi") {
            valuesToUpdate.push({
                field_definition_id: fieldMapping.field_definition_id,
                value: []
            });
        }
        // Pour les champs text ou single-select, on ne fait rien
        continue;
    }

    if (fieldMapping.type === "select") {
        const optionId = fieldMapping.options[geminiValue];
        if (optionId) {
            valuesToUpdate.push({
                field_definition_id: fieldMapping.field_definition_id,
                value: [optionId]
            });
        } else {
            console.warn(`[WARNING] Option ID not found for field "${fieldName}" with value "${geminiValue}". This field will NOT be updated.`);
        }
    } else if (fieldMapping.type === "select_multi") {
        let valueArray = [];
        if (Array.isArray(geminiValue)) {
            valueArray = geminiValue;
        } else {
            // Logique pour g√©rer les cha√Ænes concat√©n√©es
            valueArray = geminiValue.split(',').map(item => item.trim());
        }

        const optionIds = valueArray.map(val => fieldMapping.options[val]).filter(id => id);
        if (optionIds.length > 0) {
            valuesToUpdate.push({
                field_definition_id: fieldMapping.field_definition_id,
                value: optionIds
            });
        } else {
            // G√©rer le cas o√π des valeurs sont pr√©sentes mais invalides
            console.warn(`[WARNING] No valid Option IDs found for multi-select field "${fieldName}" with values "${geminiValue}". This field will NOT be updated.`);
        }
    } else if (fieldMapping.type === "text") {
        valuesToUpdate.push({
            field_definition_id: fieldMapping.field_definition_id,
            value: geminiValue
        });
    }
}


const frameioPayload = {
