// ‚úÖ √âtape 2 : D√©couper le buffer en fonction des tailles des URLs
let offset = 0;
const parts = [];

for (let i = 0; i < uploadUrls.length; i++) {
	const size = uploadUrls[i].size;

	// On d√©coupe les bons octets
	const chunk = buffer.slice(offset, offset + size);

	// On encode en base64 pour N8N (sera reconverti lors de l'upload)
	const chunkBase64 = chunk.toString('base64');

	parts.push({
