// === AGENT NOMENCLATURE IA - MAPPING DES IDS ===
// Logique anti-fragile pour validation et mapping

const items = $input.all();
const results = [];

for (const item of items) {
  try {
    const driveData = item.json;
    const geminiOutput = driveData.output;

    // Tentative de parsing JSON Gemini
    let analysisData;
    let mappingSuccess = false;
    let errorDetails = null;

    try {
      // Parse la r√©ponse Gemini
      if (typeof geminiOutput === 'string') {
        analysisData = JSON.parse(geminiOutput);
      } else {
        analysisData = geminiOutput;
      }

      // Validation des champs requis
      if (analysisData.analysis_success === false) {
        throw new Error(`Analyse Gemini √©chou√©e: ${analysisData.error}`);
      }

      // Mapping des IDs avec validation
      const productMapping = {
        'bonnet': 'BONNET',
        'skullcap': 'SKULLCAP', 
        'accessoire': 'ACCESSOIRE'
      };

      const hookMapping = {
        'testimonial': 'TESTIMONIAL',
        'demo': 'DEMO',
        'lifestyle': 'LIFESTYLE',
        'unboxing': 'UNBOXING',
        'problem_solution': 'PROBLEM_SOL'
      };

      const formatMapping = {
        'square': 'SQ',
        'vertical': 'VERT',
        'horizontal': 'HORIZ'
      };

      // Validation et mapping
      const mappedProduct = productMapping[analysisData.product_type?.toLowerCase()];
      const mappedHook = hookMapping[analysisData.hook_angle?.toLowerCase()];
      const mappedFormat = formatMapping[analysisData.format?.toLowerCase()];

      if (!mappedProduct || !mappedHook || !mappedFormat) {
        throw new Error(`Mapping incomplet: product=${mappedProduct}, hook=${mappedHook}, format=${mappedFormat}`);
      }

      // G√©n√©ration de la nomenclature
      const dateStr = new Date().toLocaleDateString('fr-FR', { month: '2-digit', day: '2-digit' }).replace('/', '');
      const nomenclature = `SATYN_${mappedProduct}_VID_${mappedFormat}_${mappedHook}_V1_${dateStr}`;

      mappingSuccess = true;

      results.push({
        json: {
          mapping_success: true,
          nomenclature_final: nomenclature,
          analysis_data: analysisData,
          mapped_values: {
            product: mappedProduct,
            hook: mappedHook,
            format: mappedFormat
          },
          original_file: {
            name: driveData.name,
            id: driveData.id,
            createdTime: driveData.createdTime
          },
          confidence_score: analysisData.confidence || 0.8,
          processing_timestamp: new Date().toISOString()
        }
      });

    } catch (parseError) {
      // √âchec du mapping - utilisation du fallback
      errorDetails = parseError.message;

      // Fallback avec nomenclature par d√©faut
      const dateStr = new Date().toLocaleDateString('fr-FR', { month: '2-digit', day: '2-digit' }).replace('/', '');
      const fallbackNomenclature = `SATYN_BONNET_VID_SQ_DEMO_V1_${dateStr}_FALLBACK`;

      results.push({
        json: {
          mapping_success: false,
          nomenclature_final: fallbackNomenclature,
          error_type: 'mapping_failure',
          error_details: errorDetails,
          fallback_used: true,
          original_analysis: geminiOutput,
          original_file: {
            name: driveData.name,
            id: driveData.id,
            createdTime: driveData.createdTime
          },
          processing_timestamp: new Date().toISOString()
        }
      });
    }

  } catch (globalError) {
    // Erreur critique - cr√©ation d'un fallback minimal
    const dateStr = new Date().toLocaleDateString('fr-FR', { month: '2-digit', day: '2-digit' }).replace('/', '');
    const emergencyNomenclature = `SATYN_UNKNOWN_VID_SQ_DEMO_V1_${dateStr}_ERROR`;

    results.push({
      json: {
        mapping_success: false,
        nomenclature_final: emergencyNomenclature,
        error_type: 'critical_failure',
        error_details: globalError.message,
        fallback_used: true,
        processing_timestamp: new Date().toISOString()
      }
    });
  }
}

return results;
