// This is the definitive, corrected code for the 'Prepare Data' node.
// It maps every field to its correct source agent from the consolidated input.

const allData = items[0].json;

// --- Helper function to safely get data blocks ---
// This handles cases where an agent's output is nested inside an "output" key.
function getDataBlock(agentData) {
  if (!agentData) return {};
  return agentData.output && typeof agentData.output === 'object' ? agentData.output : agentData;
}

// --- Helper function to parse stringified JSON arrays ---
function parseJsonString(jsonString) {
  try {
    const parsedArray = JSON.parse(jsonString);
    return parsedArray[0]?.json || {};
  } catch (e) {
    return {};
  }
}

// --- Extract data from the CORRECT source agents ---
const campaignInput = parseJsonString(allData["Campaign input"]);
const clientData = parseJsonString(allData["Client Data"]);

// The high-level strategy comes from the "Topic Insights" agent
const topicInsights = getDataBlock(allData["Topic Insights & Core Angle Synthesizer"]);

// The deep dive keywords come from the "Deep-Dive" agent
const keywordAnalysis = getDataBlock(allData["Deep-Dive Keyword Analyst"]);

// The competitor info comes from the "Competitor Identification" agent
const competitorAnalysis = getDataBlock(allData["Competitor Identification"]);

// The E-E-A-T links come from the "Authoritative Resource" agent
const eeatLinks = getDataBlock(allData["Authoritative Resource Link Finder Agent"]);


// --- Construct the Final Article Brief ---
const finalArticleBrief = {
  "article_brief": {
    "content_requirements": {
      "target_word_count": campaignInput.TargetWordCount || "2000",
