      // Key opportunities come from 'Topic Insights'
      "key_eeat_opportunities": topicInsights.key_eeat_opportunities || [],
      // Approved links come from the 'Authoritative Resource' finder
      "approved_authoritative_external_links": eeatLinks.approved_authoritative_external_links || []
    },
    "competitor_analysis": {
      "overall_competitor_landscape_summary": competitorAnalysis.overall_competitor_landscape_summary || "N/A",
      "top_identified_competitors": competitorAnalysis.top_identified_competitors || [],
      "strategic_recommendations_to_outperform": competitorAnalysis.strategic_recommendations_to_outperform || []
    },
