      "internal_linking_pages": (clientData["Internal linking pages"] || "").split('\n').filter(url => url.trim() !== '')
    }
  }
};

// Return the final brief for the next agent
return {
  json: finalArticleBrief
};

// This is the complete and corrected code for the 'Final Report Formatter' node.
// It will take the final 'article_brief' JSON and format it into a single,
// clean, human-readable Markdown text string.

const brief = items[0].json.article_brief;

// Helper function to format arrays into clean bulleted lists
function formatList(items) {
  if (!items || items.length === 0) {
    return "  * N/A";
  }
  // This handles both simple string arrays and arrays of objects with a 'name' property
  return items.map(item => {
    if (typeof item === 'string') {
        return `*   ${item}`;
    }
    return `*   ${item.name || JSON.stringify(item)}`; // Fallback for unexpected object structures
  }).join('\n');
}

// Helper function for Competitor Strengths/Weaknesses which are also arrays
function formatCompetitorLists(items) {
  if (!items || items.length === 0) {
    return "N/A";
  }
  return '\n' + items.map(item => `        *   ${item}`).join('\n');
}


// Main function to build the report string
function generateReport(data) {
  // This line was missing from the snippet, causing the error.
  const reportLines = [];

  // --- Section 1: Core Strategy ---
  reportLines.push('# **Final SEvO Research & Strategy Report**', '---');
  reportLines.push('## **SECTION 1: CORE CONTENT STRATEGY & ANGLE**\n');
  reportLines.push('### **Overarching Article Topic:**', `*   ${data.strategic_direction.overarching_article_topic}\n`);
  reportLines.push('### **Core Article Angle & Unique Value Proposition (UVP):**', `*   ${data.strategic_direction.core_article_angle_uvp}\n`);
  reportLines.push('### **Primary Target Audience Persona:**', `*   ${data.strategic_direction.primary_target_audience_persona}\n`);
  reportLines.push('### **Critical Themes to Cover:**', formatList(data.seo_elements.critical_themes_to_cover) + '\n');

  // --- Section 2: SEO Keyword Blueprint ---
  reportLines.push('---', '## **SECTION 2: SEO KEYWORD BLUEPRINT**\n');
  reportLines.push('### **Top 10 High-Value Keyword Opportunities:**');
  (data.seo_elements.top_10_keyword_opportunities || []).forEach(kw => {
    reportLines.push(`*   **Keyword:** \`${kw.keyword}\``);
    reportLines.push(`    *   **Intent:** ${kw.search_intent}`);
    reportLines.push(`    *   **Est. Volume:** ${kw.estimated_monthly_volume}`);
    reportLines.push(`    *   **Competition:** ${kw.competition_level}`);
    reportLines.push(`    *   **Strategic Value:** ${kw.strategic_value_to_client}`);
    reportLines.push(`    *   **Opportunity Score:** ${kw.opportunity_score}\n`);
  });

  // --- Section 3: Competitor Analysis ---
  reportLines.push('---', '## **SECTION 3: COMPETITIVE LANDSCAPE ANALYSIS**\n');
  reportLines.push('### **Overall Competitor Landscape Summary:**', `${data.competitor_analysis.overall_competitor_landscape_summary}\n`);
  reportLines.push('### **Top Identified Competitors (Deep Analysis):**');
  (data.competitor_analysis.top_identified_competitors || []).forEach(comp => {
    reportLines.push(`*   **Competitor:** ${comp.competitor_name} (${comp.url})`);
    reportLines.push(`    *   **Main Angle:** ${comp.content_main_angle_and_focus}`);
    reportLines.push(`    *   **Strengths:** ${formatCompetitorLists(comp.observed_strengths)}`);
    reportLines.push(`    *   **Weaknesses/Gaps:** ${formatCompetitorLists(comp.observed_weaknesses_or_gaps)}\n`);
  });
  reportLines.push('### **Strategic Recommendations to Outperform:**', formatList(data.competitor_analysis.strategic_recommendations_to_outperform) + '\n');

  // --- Section 4: E-E-A-T Resources ---
  reportLines.push('---', '## **SECTION 4: E-E-A-T & AUTHORITY RESOURCES**\n');
  reportLines.push('### **Key E-E-A-T Opportunities:**', formatList(data.eeat_elements.key_eeat_opportunities) + '\n');
  reportLines.push('### **Approved Authoritative External Links for Citation:**');
