  <div class="report-title">
    <h1>SEvO Comprehensive Research Report</h1>
    <p><strong>Client:</strong> ${clientData["Client/Brand Name"] || 'N/A'}</p>
    <p><strong>Main Topic:</strong> ${campaignInput.MainContentTopic || 'N/A'}</p>
  </div>

  <!-- Client & Campaign Data Section -->
  <div class="section">
    <h2>Campaign & Client Details</h2>
    <div class="sub-section">
      <h3>Campaign Input</h3>
      <ul>
        <li><strong>Goal:</strong> ${campaignInput.CampaignGoal}</li>
        <li><strong>Content Format:</strong> ${campaignInput.MasterContentFormat}</li>
        <li><strong>Seed Keyword:</strong> <code>${campaignInput.SeedKeywords}</code></li>
        <li><strong>Target Word Count:</strong> ${campaignInput.TargetWordCount}</li>
        <li><strong>Audience Knowledge:</strong> ${campaignInput.AudienceKnowledgeLevel}</li>
      </ul>
    </div>
    <div class="sub-section">
      <h3>Client Data</h3>
      <ul>
        <li><strong>Brand Voice:</strong> ${clientData["Brand voice tone"]}</li>
        <li><strong>E-E-A-T Highlights:</strong> ${clientData["EEAT highlights"]}</li>
        <li><strong>Internal Linking Pages:</strong> <ul>${(clientData["Internal linking pages"] || "").split('\\n').map(url => `<li><a href="${url}" target="_blank">${url}</a></li>`).join('')}</ul></li>
      </ul>
    </div>
  </div>

  <!-- Master Research Section -->
  <div class="section">
    <h2>Master Research & Synthesis</h2>
    ${(masterResearch.research_summary_by_angle || []).map(angle => `
      <div class="sub-section">
        <h3>Angle: ${angle.research_angle}</h3>
        <p>${angle.synthesized_summary}</p>
        <h4>Key Extracted Elements:</h4>
        <ul>
          <li><strong>Entities:</strong> ${angle.extracted_elements.key_entities_mentioned.join(', ')}</li>
          <li><strong>Pain Points:</strong> ${angle.extracted_elements.key_pain_points_addressed.join(', ')}</li>
          <li><strong>Solutions:</strong> ${angle.extracted_elements.key_solutions_or_benefits_highlighted.join(', ')}</li>
        </ul>
      </div>
    `).join('')}
  </div>

  <!-- Keyword Strategy Section -->
  <div class="section">
    <h2>SEvO Keyword & Content Strategy</h2>
    ${Object.keys(sevoStrategy.master_keyword_clusters || {}).map(key => `
      <div class="sub-section">
        <h3>${key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}</h3>
        <ul>
        ${sevoStrategy.master_keyword_clusters[key].map(kw => `
          <li><strong>Keyword:</strong> <code>${kw.keyword}</code><br>
          <strong>Intent:</strong> ${kw.search_intent}<br>
          <strong>Rationale:</strong> ${kw.strategic_rationale}</li>
        `).join('')}
        </ul>
      </div>
    `).join('')}
  </div>

  <!-- Deep-Dive Keyword Analysis Section -->
  <div class="section">
      <h2>Deep-Dive Keyword Analysis</h2>
      <div class="sub-section">
          <h3>Top 10 Keyword Opportunities</h3>
          <ul>
              ${(keywordAnalysis.top_10_keyword_opportunities || []).map(kw => `
                  <li>
                      <strong>Keyword:</strong> <code>${kw.keyword}</code><br>
                      <strong>Intent:</strong> ${kw.search_intent} | <strong>Volume:</strong> ${kw.estimated_monthly_volume} | <strong>Competition:</strong> ${kw.competition_level}<br>
                      <strong>Strategic Value:</strong> ${kw.strategic_value_to_client}<br>
                      <strong>Opportunity Score:</strong> ${kw.opportunity_score}/10
                  </li>
              `).join('')}
          </ul>
      </div>
  </div>

  <!-- Competitor Analysis Section -->
  <div class="section">
      <h2>Competitor Identification</h2>
      <div class="sub-section">
          <h3>Overall Summary</h3>
          <p>${competitorAnalysis.overall_competitor_landscape_summary || 'N/A'}</p>
      </div>
      <div class="sub-section">
          <h3>Top Competitors</h3>
          <ul>
              ${(competitorAnalysis.top_identified_competitors || []).map(comp => `
                  <li>
                      <strong>${comp.competitor_name}</strong> (<a href="${comp.url}" target="_blank">View Article</a>)<br>
                      <strong>Angle:</strong> ${comp.content_main_angle_and_focus}<br>
                      <strong>Weaknesses to Exploit:</strong>
                      <ul>${(comp.observed_weaknesses_or_gaps || []).map(gap => `<li>${gap}</li>`).join('')}</ul>
                  </li>
              `).join('')}
          </ul>
      </div>
  </div>

  <!-- E-E-A-T Resources Section -->
  <div class="section">
      <h2>Authoritative E-E-A-T Resources</h2>
      <ul>
          ${(eeatResources.approved_authoritative_external_links || []).map(link => `
              <li>
                  <strong>${link.source_name}:</strong> <a href="${link.url}" target="_blank">${link.title}</a><br>
                  <em>Justification:</em> ${link.brief_justification_for_inclusion}
              </li>
          `).join('')}
      </ul>
  </div>

  <!-- Draft Outline Section -->
  <div class="section">
    <h2>First Draft Outline</h2>
    ${markdownToHtml(firstDraftOutline)}
  </div>

  <!-- Final Article Section -->
  <div class="section">
    <h2>First Draft Markdown Article</h2>
    ${markdownToHtml(firstDraftArticle)}
  </div>

</body>
</html>
`;

return {
  json: {
    final_html_report: html
  }
};

