const valuesToUpdate = [];

function addValueToUpdate(fieldName, geminiValue) {
    const fieldMapping = fieldMappings[fieldName];

    // Handle cases where the value is empty, "NA", "Not applicable", "Other", "None"
    if (!fieldMapping || !geminiValue ||
        geminiValue.toLowerCase() === "na" ||
        geminiValue.toLowerCase().includes("not applicable") ||
        geminiValue.toLowerCase().includes("other") || // Handles "Other" for select fields
        geminiValue.toLowerCase().includes("none") // Handles "None" for select fields
    ) {
        return;
    }

    if (fieldMapping.type === "select") {
        const optionId = fieldMapping.options[geminiValue];
        if (optionId) {
            valuesToUpdate.push({
                field_definition_id: fieldMapping.field_definition_id,
                value: [optionId]
            });
        } else {
            console.warn(`[WARNING] Option ID not found for field "${fieldName}" with value "${geminiValue}". This field will NOT be updated.`);
        }
    } else if (fieldMapping.type === "text") {
        valuesToUpdate.push({
            field_definition_id: fieldMapping.field_definition_id,
            value: geminiValue
        });
    }
}

// Call the function for each custom field, accessing the data extracted by the AI
