addValueToUpdate("ethnicity", extractedData.ethnicity);
addValueToUpdate("age_range", extractedData.age_range);
addValueToUpdate("main_concept", extractedData.main_concept);
addValueToUpdate("product_presented", extractedData.product_presented);
