parameters.jsonBody:
={
  "model": "sonar",
  "messages": [
    {
      "role": "system",
      "content": "You are a Local SERP Analyst. Your function is to provide a concise, factual summary of the top search results for a local query. Identify the primary companies and the type of content ranking (e.g., service pages, directories). Be objective and data-focused."
    },
    {
      "role": "user",
      "content": "{{ $fromAI('simple_search') }}"
    }
  ]
}

={
  "model": "sonar-pro",
  "messages": [
    {
      "role": "system",
      "content": "You are an investigative researcher. Answer the user's specific question directly and factually based on web search results. CITE YOUR SOURCES. Provide only the direct answer and its source."
    },
    {
      "role": "user",
      "content": "{{ $fromAI('pro_search') }}"
    }
  ]
}

={
  "model": "sonar-deep-research",
  "messages": [
    {
      "role": "system",
      "content": "You are an expert SEO research assistant. Your sole purpose is to perform a deep and comprehensive keyword analysis based on the user's query. Provide a wide-ranging list of related keywords, long-tail keywords, common questions, search intent data, and an analysis of the top-ranking content. Focus on providing a large volume of raw data and facts. The user, an expert analyst, will handle the final strategic filtering and interpretation."
    },
    {
      "role": "user",
      "content": "{{ $fromAI('deep_keyword_research') }}"
    }
  ]
}

parameters.jsonSchemaExample:
{
  "initial_keyword_research": {
    "primary_keyword": "Hard Water Effects",
    "related_terms": ["hard water hair loss", "shower filter for dry skin"],
    "user_questions": ["Does hard water cause acne?"]
  },
  "research_summary_by_angle": [
    {
      "research_angle": "User Pain Points",
      "synthesized_summary": "Users consistently report issues like dry, itchy skin, dull and brittle hair, and exacerbation of eczema after showering in hard water areas. The primary complaints center around mineral buildup (limescale) and the effects of chlorine.",
      "extracted_elements": {
        "key_entities_mentioned": ["limescale", "chlorine", "eczema"],
        "key_pain_points_addressed": ["dry skin", "itchy scalp", "brittle hair", "faded hair color"],
        "key_solutions_or_benefits_highlighted": ["water softeners", "clarifying shampoos", "shower head filters"],
        "prominent_questions_raised": ["How to know if I have hard water?", "Can hard water cause hair loss?"],
        "notable_statistics_or_data_points": ["Over 85% of US homes have hard water."],
        "relevant_source_urls": [
          "https://www.healthline.com/health/hard-water-hair",
          "https://www.medicalnewstoday.com/articles/hard-water-and-skin"
        ]
      }
    }
  ],
  "aggregated_insights": {
    "overall_main_themes": [
      "Mineral Buildup & Residue",
      "Chlorine and its Irritating Effects",
      "Skin & Hair Health Impact"
    ],
    "all_unique_paa_questions": [
      "What are the signs of hard water on skin?",
      "Can you reverse hard water hair damage?"
    ],
    "all_unique_related_searches": [
      "best shower filter for hard water",
      "hard water vs soft water"
    ],
    "potential_content_gaps_or_opportunities": [
      "Detailed comparison of different filter technologies (KDF, Vitamin C, Carbon).",
      "Content focused on specific regional hard water issues (e.g., 'Texas hard water solutions')."
    ],
    "emerging_trends_or_innovations": [
      "Shower filters incorporating aromatherapy.",
      "Smart filters that track water usage and filter life."
    ],
    "client_brand_mentions_context": [
      {
        "brand": "Filter Oasis",
        "context": "Frequently referenced for its vitamin C filters designed to reduce chlorine impact on skin."
      }
    ],
    "key_competitor_signals": [
      {
        "competitor_name": "AquaBliss",
        "context_snippet": "The AquaBliss SF220 is often reviewed as a top choice for its multi-stage filtration...",
        "source_url": "https://www.forbes.com/vetted/best-shower-filters/",
        "source_tool": "google_search"
      }
    ]
  }
}

{
  "top_10_keyword_opportunities": [
    {
      "keyword": "string",
      "search_intent": "Informational",
      "estimated_monthly_volume": 0,
      "competition_level": "Low",
      "strategic_value_to_client": "Targets users with eczema, a core pain point, and allows us to leverage our testing certifications as a trust signal.",
      "opportunity_score": 8
    }
  ]
}

{
  "master_keyword_clusters": {
    "core_topic_keywords": [
      {
        "keyword": "filtered showerhead",
        "search_intent": "Informational/Commercial",
        "strategic_rationale": "This is the primary topic with high search volume. It's the central pillar for all content, targeting users at the top and middle of the funnel."
      },
      {
        "keyword": "hard water filter",
        "search_intent": "Informational/Problem-Aware",
        "strategic_rationale": "Directly addresses a primary pain point (hard water) identified in the research, capturing users actively seeking a solution."
      }
    ],
    "long_tail_specific_queries": [
      {
        "keyword": "do filtered shower heads help with eczema",
        "search_intent": "Informational/Solution-Seeking",
        "strategic_rationale": "A specific user question from PAA data. Answering this directly positions the brand as an expert resource for a high-pain-point audience."
      },
      {
        "keyword": "best shower filter for dry skin",
        "search_intent": "Commercial/Transactional",
        "strategic_rationale": "Targets users who have identified their problem and are now comparing products, indicating high purchase intent."
      }
    ],
    "user_pain_point_keywords": [
      {
        "keyword": "reduce chlorine in shower water",
        "search_intent": "Problem-Aware/Informational",
        "strategic_rationale": "Addresses the core scientific reason for skin/hair issues found in the research. Targets users who understand the root cause."
      },
      {
        "keyword": "stop hair color fading from water",
        "search_intent": "Problem-Aware/Solution-Seeking",
        "strategic_rationale": "A specific, high-value pain point for an audience segment that invests in hair treatments. Offers a clear ROI for the user."
      }
    ],
    "competitive_landscape_keywords": [
      {
        "keyword": "jolie shower head alternative",
        "search_intent": "Commercial/Comparative",
        "strategic_rationale": "Targets users aware of a major competitor (Jolie) but seeking other options, potentially due to price or features. This allows for direct comparison."
      },
      {
        "keyword": "aquabliss vs filter oasis",
        "search_intent": "Commercial/Comparative",
        "strategic_rationale": "Creates an opportunity for a direct, feature-by-feature comparison against another key competitor, capturing bottom-of-funnel traffic."
      }
    ]
  },
  "sevo_content_angle_recommendations": [
    {
      "platform": "Main Website Blog",
      "suggested_keywords_for_platform": [
        "best shower filter for hard water",
        "do filtered shower heads help with eczema",
        "kdf vs carbon filter"
      ],
      "suggested_hashtags": [],
      "proposed_content_angle": "The Ultimate Guide to Choosing a Shower Filter for Your Home. This comprehensive article should address the science behind filtration (KDF, Carbon), help users diagnose their water type (hard vs. soft), and provide a detailed breakdown of benefits for specific conditions like eczema and dry skin, leveraging the client's certifications as proof points.",
      "content_format_and_style_tip": "Create a long-form, data-driven article featuring an interactive 'Find Your Filter' quiz. Use clear headings, bullet points, and high-quality images. Embed customer testimonials and links to the scientific studies mentioned in the research."
    },
    {
      "platform": "YouTube",
      "suggested_keywords_for_platform": [
        "filtered shower head review",
        "shower filter installation",
        "hard water hair before and after"
      ],
      "suggested_hashtags": [
        "#showerfilter",
        "#hardwater",
        "#haircare",
        "#skincare"
      ],
      "proposed_content_angle": "We Tested a Filtered Showerhead for 30 Days: SEE The Results! This video should visually demonstrate the 'before and after' effects on hair (frizz, shine) and skin. Include a simple installation guide and a clear explanation of how the filter works to combat chlorine and minerals.",
      "content_format_and_style_tip": "Create a visually engaging, fast-paced video (5-7 minutes). Use on-screen text callouts to highlight key benefits. Feature a relatable host and show close-ups of the water flow and filter components."
    },
    {
      "platform": "TikTok",
      "suggested_keywords_for_platform": [
        "tiktok made me buy it shower filter",
        "what's in my tap water",
        "dry skin hack"
      ],
      "suggested_hashtags": [
        "#tiktokmademebuyit",
        "#beautytips",
        "#skinhacks",
        "#cleantok"
      ],
      "proposed_content_angle": "The Gross Reason Your Skin is So Dry. A short, impactful video showing a water quality test (e.g., chlorine test strips turning a dark color) on regular tap water vs. water from the filtered showerhead. The contrast provides a 'shock factor' that drives engagement and highlights the problem.",
      "content_format_and_style_tip": "A quick, vertical video (under 60 seconds) using trending audio. Focus on a single, powerful visual and use bold, easy-to-read text overlays. End with a clear call-to-action linking to the product."
    }
  ]
}

